import React from 'react';
import { connect } from 'react-redux';
import { Form , Input } from 'antd';
import * as actions from '../../store/actions/auth'
import GridLoader from "react-spinners/ClipLoader"

class LoginForm extends React.Component {

    handleFinish = values => {
        this.props.onAuth(values.username, values.password)
        .then(() => {
        if (!this.props.error) {
            window.location.href = "/expense";
        }
        })
    }

    render() {
        let errorMessage = null;
        if (this.props.error) {
            errorMessage = (
                <p className = "error">Invalid username or password. Please try again.</p>
            )
        }
        return (
<Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7}>
      <Typography component="h1" variant="h2">
            I AM A VC LOOKING FOR STARTUPS TO INVEST IN
          </Typography>
      </Grid>
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            StartUp Sign in
            { props.loading && 
                <div>    
            <h4 style={{float:'left',marginRight:'1%',marginLeft:'2%'}}>Loading </h4>
            <GridLoader size={30} css={{float:'left',marginBottom:"2%"}}/>
            </div>
            }
            {errorMessage}
          </Typography>
          <form className={classes.form} noValidate onSubmit={onSubmit}>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container justify="center">
              <Grid item>
                <Link href="/register" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
      </Grid>
    </Grid>
        )
    }

}

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, password) => {
            return dispatch(actions.authLogin(username,password))
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(LoginForm);
