import React from 'react'
import axios from 'axios';
import { connect } from 'react-redux';

class AddTransaction extends React.Component {

    onSubmit = (event,type,id) => {
      event.preventDefault();
      axios.defaults.headers = {
        "Content-Type":"application/json",
        Authorization: this.props.token
    }
    const name = event.target.name.value;
    const amount = event.target.amount.value;
    const username = localStorage.getItem("username");
    switch ( type ) {
      case 'post':
          return axios.post('api/', {name: name, username: username,
          amount: amount})
          .catch(err => console.log(err))
      case 'put':
          return axios.put(`api/${id}/`, {name: name, username: username,
          amount: amount})
          .catch(err => console.log(err));
      default:
          return
  }
    }
    render() {
    return (
        <div>
        <h3>Add new transaction</h3>
      <form onSubmit={event => this.onSubmit(event,this.props.type, this.props.id)}>
        <div className ="form-control">
          <label htmlFor="text">Category</label>
          <input type="text" name="name" placeholder="Enter..." />
        </div>
        <div className="form-control">
          <label htmlFor="amount"> Expense <br />
          (negative for expense, positive for revenue)
            </label>
          <input type="number" name="amount" 
          placeholder="Enter amount..." />
        </div>
        <button className="btn">Add transaction</button>
      </form>
    </div>
    )
    }
}

const mapStateToProps = state => {
  return {
      token: state.token
  }
}

export default connect(mapStateToProps)(AddTransaction);
