import React from 'react';
import { Form, Input, Button} from 'antd';
import axios from 'axios';

axios.defaults.baseURL = 'http://127.0.0.1:8000/';

class ExpenseForm extends React.Component {

    handleFormSubmit = (event, type, id) => {
        event.preventDefault();
        const name = event.target.elements.name.value;
        const amount = event.target.elements.amount.value;
        switch ( type ) {
            case 'post':
                return axios.post('api/', {name: name,
                amount: amount}).then(res => console.log(res))
            case 'put':
                return axios.put(`api/${id}/`, {name: name,
                amount: amount})
        }
    }

    render() {
        return (
        <div>
            <Form onSubmit={(event) => this.handleFormSubmit(
                event, this.props.type, this.props.id)}>
                <Form.Item label="Description">
                <Input name = "name" placeholder="Input expense description" />
                </Form.Item>
                <Form.Item label="Amount">
                <Input name = "amount" placeholder="Input content" />
                </Form.Item>
                <Form.Item>
                <Button type="primary" htmlType="submit">{this.props.btnText}</Button>
                </Form.Item>
            </Form>
        </div>
        )
    }
};

export default ExpenseForm
