import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';

class Profile extends React.Component {

    state = {
        profile: null
    }

    componentDidMount() {
        setTimeout(() => 
          axios.get(`profile/${localStorage.getItem("username")}/`)
          .then(prof => {
              this.setState({
                  profile: prof
              });
          }), 200);
      }

      onSubmit = event => {
        const company_name = event.target.company_name.value;
        const company_description = event.target.company_description.value;
        const show_public = event.target.show_public.value;
        return axios.put(`profile/${localStorage.getItem("username")}/`, 
        {company_name: company_name, company_description: company_description, show_public: show_public})
            .catch(err => console.log(err))
    }

    switchChange = checked => {
        this.setState({ show_public: checked });
    }

    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <label>Company Name:</label>
                <input type="text" name = "company_name"/>
                <br/>
                <label>Company Industry:</label>
                <input type = "text" name = "company_name"/>
                <br/>
                <label>Company Description:</label>
                <input type = "text"/>
                <br/>
                <label>Show profile to Public:</label>
                <input type="checkbox"/>
                <br/>
                <button type="submit">Update Profile</button> 
            </form>
        )
}
}

const mapStateToProps = state => {
    return {
        token: state.token
    }
  }

  export default connect(mapStateToProps)(Profile);
