import React from 'react';
import axios from 'axios';

axios.defaults.baseURL = 'http://127.0.0.1:8000/';

class IncomeExpenses extends React.Component {
  amounts = this.props.data.map(transaction => transaction.amount);

  total = this.amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);

  income = this.amounts
    .filter(item => item > 0)
    .reduce((acc, item) => (acc += item), 0)
    .toFixed(2);

  length = this.props.data.length

  expense = (
    this.amounts.filter(item => item < 0).reduce((acc, item) => (acc += item), 0) *
    -1
  ).toFixed(2);

  render() {
  return (
        <div className="inc-exp-container">
        <div>
          <h4>Revenue</h4>
    <p className="money plus">{this.income}</p>
        </div>
        <div>
          <h4>Expense</h4>
          <p className="money minus">{this.expense}</p>
        </div>
        <div>
          <h3>Total Transactions</h3>
          <p className="money total">{this.props.data.length}</p>
        </div>
        <div>
          <h3>Nett Revenue</h3>
          <p className="money total">{this.total}</p>
        </div>

      </div>
    )
  }
}

export default IncomeExpenses;
