import React from 'react';
import axios from 'axios';
import { Button, Form } from 'react-bootstrap'

axios.defaults.baseURL = 'http://127.0.0.1:8000/';

class ExpenseForm extends React.Component {

    handleFormSubmit = (event, type, id) => {

        event.preventDefault();
        const name = event.target.name;
        const amount = event.target.amount;
        switch ( type ) {
            case 'post':
                return axios.post('api/', {name: name,
                amount: amount}).then(res => console.log(res))
                .catch(err => console.log(err))
            case 'put':
                return axios.put(`api/${id}/`, {name: name,
                amount: amount})
            default:
                console.log("Error")

        }
    }


    render() {
        return (
 <Form onSubmit = {(event) => this.handleFormSubmit(event, this.props.type, this.props.id)}>
    <label>Expense Description</label>
    <input type="name"  placeholder="Enter expense description"/>

    <label>Password</label>
    <input type="amount"  placeholder="Input Amount"/>

  <Button type="submit" class="btn btn-primary">{this.props.btnText}</Button>
</Form>

        )
    }
};

export default ExpenseForm
