import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import * as actions from '../../store/actions/auth'
import GridLoader from "react-spinners/ClipLoader"
import {Redirect} from 'react-router-dom';
import './login.css';
import startup_logo from "./startup_logo.png"
import vc_background from "./vc_background.jpg"



const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(0, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: "teal"
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function Login(props) {
  const classes = useStyles();

  const onSubmit = e => {
    e.preventDefault();
    const username = e.target.username.value
    const password = e.target.password.value
    props.onAuth(username, password)
    .then(() => {
      if (localStorage.getItem("username")) {
        window.location.href = "/expense";
    }
    })
}

let errorMessage = null;
if (props.error) {
    errorMessage = (
        <p className = "error">Invalid username or password. Please try again.</p>
    )
}

  return (

    <div>
    {localStorage.getItem("username") && 
    <Redirect to="/expense"/>
    }
    <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7} style={{backgroundImage:url}}>
      <Typography component="h1" variant="h2" >
            I AM A VC LOOKING FOR STARTUPS TO INVEST IN
          </Typography>
      </Grid>
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square style={{backgroundColor:"#e8f4f8"}} align="center">
        <img src={startup_logo} alt="Startup Logo" style={{width:"500px",marginTop:"5%"}}/>
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOpenIcon />
          </Avatar>
          <Typography component="h1" variant="h4">
            Sign In
            { props.loading && 
                <div>    
            <h4 style={{marginLeft:'20%',marginTop:"5%"}}>Loading </h4>
            <GridLoader size={30} css={{marginLeft:"40%"}}/>
            </div>
            }
            {errorMessage}
          </Typography>
          <form className={classes.form} noValidate onSubmit={onSubmit}>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              label="Username"
              name="username"
              autoFocus
              autoComplete='off'
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              autoComplete="current-password"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign In
            </Button>
            <Grid container justify="center">
              <Grid item>
                <Link href="/register" variant="body1">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </form>
        </div>
      </Grid>
    </Grid>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
      loading: state.loading,
      error: state.error
  }
}

const mapDispatchToProps = dispatch => {
  return {
      onAuth: (username, password) => {
          return dispatch(actions.authLogin(username,password))
      }
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Login);
