import React from 'react';
import axios from 'axios';
import StarRatings from 'react-star-ratings';
import Spacer from 'react-add-space';
axios.defaults.baseURL = 'http://127.0.0.1:8000/';


export class Summary extends React.Component {



  render() {

  const achievement_arr = this.props.profile.achievement.split(",")
  const achievements = []
  for (const [index,value] of achievement_arr.entries()) {
    achievements.push(<p style={{fontSize:20}}>{index + 1}: {}</p>)
  }

  return (
    <>
    <text style= {{fontSize:25, color:'DarkSlateGray', textDecoration:"underline"}}>
    Company's Achievement    </text>
    <div style={{height:"20px"}}/>
    {
      this.props.data.profile.achievement.split(",").map(x => <p style={{fontSize:20}}>{x}</p>)

    }

    <Spacer amount={10} />
    <div>
    <text style= {{fontSize:20, color:'DarkSlateGray'}}>
    Ratings
    </text>
    </div>

        <Spacer amount={10} />
        <StarRatings
          rating={this.props.data.profile.ratings}
          starRatedColor="yellow"
          numberOfStars={5}
          starDimension="30px"
          name='rating'
        />

</>

    )
  }
}



  export default Summary;
