import React, { useRef } from 'react';
import * as actions from '../../store/actions/auth'
import { connect } from 'react-redux'; 
import './Login.css';
import {useForm} from "react-hook-form";
import { useHistory } from "react-router"; 

function RegisterForm({ onAuth }) {
  const { register, handleSubmit, errors, watch } = useForm();
  const password = useRef({});
  password.current = watch("password", "");

  const onSubmit = e => {
        onAuth(e.username, 
          e.email,
          e.password,
          e.confirm)
         this.history.push('/');
    }

      return (
        <form onSubmit = {handleSubmit(onSubmit)}>
          <input type="text" placeholder="Username" name="username" ref={register({required:true})} />
          {errors.username && <p>Username is required!</p>}
        <br/>
          <input type="text" placeholder="Email" name="email" ref={register({required:true, pattern:/^[\w-]+@([\w-]+\.)+[\w-]{2,4}$/g})}/>
          {errors.email && <p>Email is invalid!</p>}
        <br/>
          <input
            type = "password"
            placeholder="Password"
            name="password"
            ref={register({required:true,minLength:8, pattern:/[^\w\d]*(([0-9]+.*[A-Za-z]+.*)|[A-Za-z]+.*([0-9]+.*))/})}
            />
            {errors.password && <p>Password must contain letters and numbers and have at least 8 characters</p>}
          <br/>
          <br/>
          <input
        name="confirm"
        placeholder = "Confirm Password"
        type="password"
        ref={register({
          validate: value =>
            value === password.current
        })}
      />
      {errors.confirm && <p>The passwords do not match</p>}
      <br/>
      <br/>
      <button type = "submit">Sign Up</button>
        </form>
      );

}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, email, password1, password2) => {
            dispatch(actions.authSignup(username,email, password1,password2))
        }
    }
}

export default connect(null,mapDispatchToProps)(RegisterForm);
